mapper.select:
select count(*)
from app_info
where APKName=#{APKName}

select a.id,softwareName,APKName,softwareSize,f.valueName as flatformName,categoryLevel1Name,categoryLevel2Name,categoryLevel3Name,s.valueName as statusName,downloads,versionNo, status,versionid from app_info a left join (select * from data_dictionary where typeName='所属平台') f on a.flatformId=f.valueId left join (select * from data_dictionary where typeName='APP状态') s on a.status=s.valueId left join (select id,categoryName as categoryLevel1Name from app_category where parentId is NULL) c1 on a.categoryLevel1=c1.id left join (select id,categoryName as categoryLevel2Name from app_category where parentId IN (1,2)) c2 on a.categoryLevel2=c2.id left join (select id,categoryName as categoryLevel3Name from app_category where parentId not IN (1,2)) c3 on a.categoryLevel3=c3.id left join app_version v on a.versionId=v.id
and softwareName like '%' #{softwarename} '%'
and status=#{status}
and flatformId=#{flatformid}
and CategoryLevel1=#{categorylevel1}
and CategoryLevel2=#{categorylevel2}
and CategoryLevel3=#{categorylevel3}
